<div class="box1 box_white1"> <div id="category_news_box" > <div class="main_news_box_row"> <h3 class="box_title_policy title_policy">أهم الأخبار</h3> </div> <div id="latest_news_two" class="box_two box_white_two"> <?php $__currentLoopData = $newsR00; $__env->addLoop($__currentLoopData); foreach($__currentLoopData as $article): $__env->incrementLoopIndices(); $loop = $__env->getLastLoop(); ?> <div class="short_policy"> <div class="short_holder_policy"> <?php if($article->image): ?> <div class="image"> <a href="<?php echo e(Common::article_link($article)); ?>"> <img src="<?php echo e($fileRepo->getSmall($article->image,true,$article->md5_file)); ?>" width="135" height="90" alt="<?php echo e($article->image_caption?$article->image_caption:$article->title); ?>" /><br /> </a> </div> <?php endif; ?> <h2 class="article_title_two_one"><a href="<?php echo e(Common::article_link($article)); ?>"><?php echo e($article->title); ?></a></h2> <p> <?php echo e($article->abstract?$article->abstract:Common::subWords($article->body,25)); ?> <?php if($article->body): ?>...<?php endif; ?> <?php if(!$article->link): ?> <?php if($article->body): ?> <span class="mor_full"><a href="<?php echo e(Common::article_link($article)); ?>"> <?php echo e(Config::get("site.lang.LNG_FULL_STORY")); ?></a></span> <?php endif; ?> <?php else: ?> <a class="visit" href="<?php echo e($article->link); ?>"><img src="<?php echo e($fileRepo->getDesktopUrl("img/external.png")); ?>" alt="<?php echo e(Config::get("site.lang.LNG_VISIT_WEBSITE")); ?>" /></a> <?php endif; ?> </p> </div> </div> <?php endforeach; $__env->popLoop(); $loop = $__env->getLastLoop(); ?> </div> </div> </div><?php /**PATH C:\xampp_7.4\htdocs\akhbarona\resources\views/frontend/desktop/box/news_r00.blade.php ENDPATH**/ ?>